import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';

import BlogTitle from '../atoms/blog-TitleTextArea';
import BlogContent from "../atoms/blog-ContentTextArea";
import HeaderTag from '../atoms/HeaderTag';
import CommentTag from './blog-Comment';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    '& > *': {
      margin: theme.spacing(1),
      width: theme.spacing(70),
      height: theme.spacing(80),
    },
  },
}));

export default function SimplePaper(props) {
  const classes = useStyles();
  const blog = props.blog;

  return (
    <div className={classes.root}>
      <Paper elevation={3} >
        <BlogTitle blogid={blog.blogid} title={blog.title}/>
        <HeaderTag creationdate ={blog.creationdate} refuser={blog.refuser} />
        <BlogContent blogid={blog.blogid} content={blog.content} />
        <CommentTag blog={blog} handleOnComment={this.handleOnComment}/>
      </Paper>
    </div>
  );
}
